triggers.command:
const { max, value } = this.item.system.resource;
const maxValue = actor.system.levelData.level.current;
const afterUpdate = value+1;
if (afterUpdate > maxValue) return;

ui.notifications.info(game.i18n.localize('DAGGERHEART.UI.Notifications.knowTheTide'));
return { updates: [{
  key: 'resource',
  itemId: this.item.id,
  target: this.item,
  value: 1,
}]};
